import React from "react";
import Card from "../UI/Card";
import classes from "./Login.module.css";
const LoginPage = () =>{
window.onload= () => {
    const user = document.querySelector(".username");
    const pass = document.querySelector(".password");
    const login_btn = document.querySelector(".btn-login");
    login_btn.addEventListener('click',Req_send);
} 
 return(
    <React.Fragment>
        <Card>    
            <h1 className={classes.header_pos}>SIGN IN</h1>
                <form action="Login/Authenticate" method="Post" className= {classes['form_test']}>
                    <div className={classes.input_container}>
                        <input className={classes.username} placeholder="Username" name="usr"></input>
                        <input className={classes.password} placeholder="password" name="pwd"></input>
                    </div>  
                    <button className={classes['btn-login']}>Sign In</button>
                </form>
         </Card>       
    </React.Fragment>
        )
}

const Req_send = () => {
    var xhr= new XMLHttpRequest();
    xhr.open("POST","https://3000/Login/Authenticate");
    xhr.send(JSON.stringify({
        user : "username",
        password : "password"
    }))
}

export default LoginPage;
