import React from "react";
import Card from "../UI/Card";
import classes from "./Login.module.css";
const LoginPage = () =>{
    
    const req_handler = (e) => {
        e.preventDefault();
        var xhr= new XMLHttpRequest();
        xhr.open("POST","https://3000/Login/Authenticate");
        xhr.send(JSON.stringify({
            user : "username",
            password : "password"
        }))
    }   

 return(
     
    <React.Fragment>
        <Card>    
            <h1 className={classes.header_pos}>SIGN IN</h1>
                <form onSubmit={req_handler} className= {classes['form_test']}>
                    <div className={classes.input_container}>
                        <input className={classes.username} placeholder="Username" name="usr"></input>
                        <input className={classes.password} placeholder="password" name="pwd"></input>
                    </div>  
                    <button className={classes['btn-login']}>Sign In</button>
                </form>
         </Card>       
    </React.Fragment>
        )
}



export default LoginPage;
